import { observable, computed, action } from 'mobx';

@observable
export class GalaxyCardStore {

@observable
public x: number = 0;

@observable
public y: number = 0;

@observable
public url: string = "";

@observable
public constellation: string = "";

@observable
public information: string = "";

@observable
public side: 'a'| 'b' | 'c' = 'b';

@observable
public wish: string = "";

@computed
    public get transform(): string {
    return "translate(" + this.x + "px," + this.y + "px)"; // for CSS transform property
}

constructor(url: string, constellation: string, information: string, x: number, y: number) {
    this.url = url;
    this.constellation = constellation;
    this.information = information;
    this.x = x;
    this.y = y;
}

@action
public setSide(side: 'a' | 'b' | 'c') {
    this.side = side;
}

@action
public setWish(wish: string) {
    this.wish = wish;
}

}
